# Fix circleci repo reference
# https://community.sonarsource.com/t/code-is-empty-on-pull-request-reviews/822/11


mkdir -p ~/.ssh;
echo 'github.com ssh-rsa ' > ~/.ssh/known_hosts;

git fetch --all;
git branch -D $BC_PR_BASE_BRANCH;
git rev-parse origin/$BC_PR_BASE_BRANCH;

git checkout .;
git checkout $BC_PR_BASE_BRANCH;
git pull;
git checkout $BC_PR_BRANCH;